import { ChangeDetectionStrategy, Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { WorkersService } from '../../services/workers.service';
import { IconService } from '../../services/IconService';
import { IWorker } from '../../interfaces/worker.interface';

const cloudIcon: string = `<svg width="91" height="67" viewBox="0 0 91 67" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
<rect width="91" height="67" fill="url(#pattern0)"/>
<defs>
  <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
  <use xlink:href="#image0_76_1496" transform="matrix(0.000509173 0 0 0.000691563 -0.0017897 0)"/>
  </pattern>
  <image id="image0_76_1496" width="1971" height="1446" xlink:href="data:image/png;base64,"/>
  </defs>
  </svg>
`;

export type Card = {
  controlName: string,
  label: string,
}

@Component({
  selector: 'admin-recruitment',
  templateUrl: './recruitment.component.html',
  styleUrls: ['./recruitment.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class RecruitmentComponent{

  public readonly patternForValidationName: string = '^(?=.*[--A-Za-z])[--A-Za-z]{2,}$';

  public recruitmentForm: FormGroup = new FormGroup({
    name: new FormControl('', [Validators.required, Validators.pattern(this.patternForValidationName),Validators.maxLength(15)]),
    surname: new FormControl('', [Validators.required, Validators.pattern(this.patternForValidationName),Validators.maxLength(20)]),
    patronymic: new FormControl('', [Validators.required, Validators.pattern(this.patternForValidationName),Validators.maxLength(15)]),
    email: new FormControl('', [Validators.required, Validators.email]),
    telephone: new FormControl('', [Validators.required, Validators.pattern(/^\+7\d{10}$/)]),
    position: new FormControl('', Validators.required),
    dateOfBirth: new FormControl('', Validators.required),
    completedEducationalInstitution: new FormControl('', Validators.required),
    education: new FormControl('', Validators.required),
    place: new FormControl('', Validators.required),
    currentSalary: new FormControl('', Validators.required)
  });

  public readonly cardsOfInputs: Card[] = [
    { controlName: 'name', label: '' },
    { controlName: 'surname', label: '' },
    { controlName: 'patronymic', label: '' },
    { controlName: 'email', label: 'Email' },
    { controlName: 'telephone', label: '' },
    { controlName: 'position', label: '' },
    { controlName: 'dateOfBirth', label: ' ' },
    { controlName: 'completedEducationalInstitution', label: '. ' },
    { controlName: 'education', label: '' },
    { controlName: 'place', label: ' ' },
    { controlName: 'currentSalary', label: '' }
  ];



  constructor(private _workersService: WorkersService, private _iconService: IconService) {
    this._iconService.add('cloud', cloudIcon);
  }

  public trackByControlName(index:number, card: Card): string {
    return card.controlName;
  }

  public submitRecruitment(): void {
    this._workersService.setWorker(this.recruitmentForm.value, <IWorker>{});
    this.recruitmentForm.reset();
  }
}
